'use client'

import { getPost } from '@/app/_api/post'
import { Suspense } from 'react'
import Image from 'next/image'
import styles from './Post.module.scss'
import { API_PUBLIC_URL } from '@/app/_common/constants'

type Props = {
    params: { post_id: number }
    searchParams: { [key: string]: string | string[] | undefined }
}

export default async function Page({ params, searchParams }: Props) {
    const post = await getPost(params.post_id)

    return (
        <div className={styles.container}>
            <div className={styles.imageContainer}>
                <Image
                    src={`${API_PUBLIC_URL}/image/${post.imageId}`}
                    alt='style'
                    unoptimized={true}
                    priority={true}
                    width={600}
                    height={800}
                    sizes='100%'
                />
            </div>
            <div className={styles.header}>
                <div className={styles.profileInfo}>
                    <div className={styles.profilePic}>
                        <img
                            src='https://private-user-images.githubusercontent.com/60287070/356863617-dd8f7943-e1bc-4003-bf45-78b5346b77e5.png?jwt=eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..2dduoN_3V1q1smBhvRAeTxMbrwpKVaLXxABmoFg_wXM'
                            alt='profile'
                        />
                    </div>
                    <span className={styles.username}>{post.nickname}</span>
                </div>
                <button className={styles.followButton}>팔로우</button>
            </div>
            <div className={styles.content}>{post.postContent}</div>
            <Suspense fallback={<div>Loading...</div>}>
                <div className={styles.hashtags}>{post.hashtagContents}</div>
            </Suspense>
        </div>
    )
}
